import React, { useState } from 'react';
import { LogIn, UserPlus, Sparkles, GraduationCap, Lock, Mail, User, Key } from 'lucide-react';
import { useNavigate } from "react-router-dom";

// Mock auth functions - replace with your actual auth implementation
const useAuth = () => ({
  login: async (email, password) => {
    // Replace with actual API call
    const response = await fetch('http://localhost:5000/api/auth/login', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password })
    });
    const data = await response.json();
    if (response.ok) {
      return { success: true, user: data.user };
    }
    return { success: false, error: data.error };
  },
  register: async (name, email, password, accessCode) => {
    // Replace with actual API call
    const response = await fetch('http://localhost:5000/api/auth/register', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, email, password, access_code: accessCode })
    });
    const data = await response.json();
    if (response.ok) {
      return { success: true, message: data.message };
    }
    return { success: false, error: data.error };
  }
});

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    accessCode: ''
  });
  const [hasAccessCode, setHasAccessCode] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [loading, setLoading] = useState(false);

  const { login, register } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setMessage({ type: '', text: '' });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage({ type: '', text: '' });

    try {
      if (isLogin) {
        const result = await login(formData.email, formData.password);
        if (result.success) {
          setMessage({ type: 'success', text: 'Login successful! Redirecting...' });
        } else {
          setMessage({ type: 'error', text: result.error || 'Invalid credentials' });
        }
      } else {
        const result = await register(
          formData.name,
          formData.email,
          formData.password,
          hasAccessCode ? formData.accessCode : null
        );
        if (result.success) {
          setMessage({ type: 'success', text: 'Account created! Please log in.' });
          setTimeout(() => setIsLogin(true), 2000);
        } else {
          setMessage({ type: 'error', text: result.error || 'Registration failed' });
        }
      }
    } catch (error) {
      setMessage({ type: 'error', text: 'Something went wrong. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-6">
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-500/20 rounded-full blur-3xl" style={{ animation: 'pulse 4s ease-in-out infinite' }}></div>
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-cyan-500/20 rounded-full blur-3xl" style={{ animation: 'pulse 4s ease-in-out infinite', animationDelay: '2s' }}></div>
      </div>

      <div className="w-full max-w-md relative z-10">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-purple-500 to-pink-500 rounded-3xl mb-4 shadow-2xl shadow-purple-500/50">
            <Sparkles className="w-10 h-10 text-white" />
          </div>
          <img
            src="/assets/logo.png"   // ← this matches your path
            alt="NextLogicAI Logo"
            className="h-60 w-auto object-contain drop-shadow-2xl"
         />
          <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-2">
            NextLogicAI
          </h1>
          <p className="text-white/60">AI-Powered Educational Platform</p>
        </div>

        <div className="bg-white/5 backdrop-blur-xl rounded-3xl p-8 border border-white/10 shadow-2xl">
          <div className="flex gap-2 bg-white/5 p-2 rounded-2xl mb-8">
            <button
              onClick={() => {
                setIsLogin(true);
                setMessage({ type: '', text: '' });
              }}
              className={`flex-1 py-3 rounded-xl font-semibold transition-all duration-300 flex items-center justify-center gap-2 ${
                isLogin
                  ? 'bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg'
                  : 'hover:bg-white/5'
              }`}
            >
              <LogIn className="w-4 h-4" />
              Login
            </button>
            <button
              onClick={() => {
                setIsLogin(false);
                setMessage({ type: '', text: '' });
              }}
              className={`flex-1 py-3 rounded-xl font-semibold transition-all duration-300 flex items-center justify-center gap-2 ${
                !isLogin
                  ? 'bg-gradient-to-r from-purple-500 to-pink-500 shadow-lg'
                  : 'hover:bg-white/5'
              }`}
            >
              <UserPlus className="w-4 h-4" />
              Sign Up
            </button>
          </div>

          <div className="space-y-5">
            {!isLogin && (
              <div>
                <label className="block text-sm font-semibold mb-2 text-white/60">
                  Full Name
                </label>
                <div className="relative">
                  <User className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="John Doe"
                    className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                  />
                </div>
              </div>
            )}

            <div>
              <label className="block text-sm font-semibold mb-2 text-white/60">
                Email Address
              </label>
              <div className="relative">
                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="you@school.edu"
                  className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-semibold mb-2 text-white/60">
                Password
              </label>
              <div className="relative">
                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="••••••••"
                  className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                />
              </div>
            </div>

            {!isLogin && (
              <>
                <div className="flex items-center gap-3">
                  <input
                    type="checkbox"
                    id="hasCode"
                    checked={hasAccessCode}
                    onChange={(e) => setHasAccessCode(e.target.checked)}
                    className="w-5 h-5 rounded bg-white/5 border-white/20 text-indigo-500 focus:ring-indigo-500"
                  />
                  <label htmlFor="hasCode" className="text-sm text-white/60 flex items-center gap-2">
                    <GraduationCap className="w-4 h-4" />
                    I have a school access code
                  </label>
                </div>

                {hasAccessCode && (
                  <div>
                    <label className="block text-sm font-semibold mb-2 text-white/60">
                      Access Code
                    </label>
                    <div className="relative">
                      <Key className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                      <input
                        type="text"
                        name="accessCode"
                        value={formData.accessCode}
                        onChange={handleChange}
                        placeholder="ABC123DEF"
                        className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all font-mono"
                      />
                    </div>
                  </div>
                )}
              </>
            )}

            {message.text && (
              <div className={`p-4 rounded-xl text-sm ${
                message.type === 'success'
                  ? 'bg-green-500/20 border border-green-400/30 text-green-400'
                  : 'bg-red-500/20 border border-red-400/30 text-red-400'
              }`}>
                {message.text}
              </div>
            )}

            <button
              onClick={handleSubmit}
              disabled={loading}
              className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed py-4 rounded-xl font-bold text-lg shadow-lg hover:shadow-xl hover:scale-102 transition-all duration-500 flex items-center justify-center gap-3"
            >
              {loading ? (
                <>
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                  {isLogin ? 'Logging in...' : 'Creating account...'}
                </>
              ) : (
                <>
                  {isLogin ? <LogIn className="w-5 h-5" /> : <UserPlus className="w-5 h-5" />}
                  {isLogin ? 'Login' : 'Create Account'}
                </>
              )}
            </button>
          </div>

          {!isLogin && !hasAccessCode && (
            <div className="mt-6 p-4 bg-blue-500/10 border border-blue-400/20 rounded-xl text-sm text-blue-200">
              <p className="font-semibold mb-1">Without an access code:</p>
              <p className="text-blue-200/70">You'll get 3 free uses. Upgrade for unlimited access!</p>
            </div>
          )}
        </div>

        <p className="text-center text-white/30 text-sm mt-6">
          By continuing, you agree to our Terms of Service
        </p>
      </div>
    </div>
  );
};

export default AuthPage;
