// src/components/AuthPage.js
import React, { useState, useEffect } from "react";
import {
  LogIn,
  UserPlus,
  Mail,
  Lock,
  User,
  Key,
  GraduationCap,
} from "lucide-react";
import { useAuth } from "../AuthContext";
import { useNavigate } from "react-router-dom";

export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: "", email: "", password: "", accessCode: ""
  });
  const [hasAccessCode, setHasAccessCode] = useState(false);

  const { login, register, loading, error, user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (user) navigate("/dashboard", { replace: true });
  }, [user, navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      await login(formData.email, formData.password);
    } else {
      await register(formData.name, formData.email, formData.password, hasAccessCode ? formData.accessCode : null);
      setIsLogin(true);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-slate-900 to-purple-800 flex items-center justify-center p-6 relative overflow-hidden">
      
      {/* Animated Blobs */}
      <div className="absolute inset-10 pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-600/30 rounded-full blur-3xl animate-pulse" />
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-cyan-600/30 rounded-full blur-3xl animate-pulse" style={{ animationDelay: "2s" }} />
      </div>

      {/* === CENTERED CONTENT === */}
      <div className="relative z-10 w-full max-w-md">

        {/* === LOGO (Centered) === */}
        <div className="flex justify-center mb-8">
          <img
            src="/assets/logo.png"
            alt="NextLogic AI"
            className="h-60 w-auto object-contain drop-shadow-2xl"
          />
        </div>

        {/* === TITLE (Centered) === */}

        {/* === TRANSPARENT GLASS BOX === */}
        <div className="bg-white/10 backdrop-blur-2xl border border-white/20 rounded-3xl p-8 shadow-2xl">
        <div className="text-center mb-10">
          <h1 className="text-6x2 font-bold bg-gradient-to-br from-purple-900 via-slate-400 to-purple-900 bg-clip-text text-transparent">
            NextLogic
          </h1>
          <p className="text-white/50 mt-6">AI Powered Educational Platform</p>
        </div>

          {/* Toggle */}
          <div className="flex gap-2 bg-white/10 p-2 rounded-2xl mb-8">
            <button
              onClick={() => setIsLogin(true)}
              className={`flex-1 py-3 rounded-xl font-bold flex items-center justify-center gap-2 transition-all ${
                isLogin
                  ? "bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-md"
                  : "text-white/60 hover:bg-white/10"
              }`}
            >
              <LogIn className="w-5 h-5" /> Login
            </button>
            <button
              onClick={() => setIsLogin(false)}
              className={`flex-1 py-3 rounded-xl font-bold flex items-center justify-center gap-2 transition-all ${
                !isLogin
                  ? "bg-gradient-to-r from-purple-500 to-pink-500 text-white shadow-md"
                  : "text-white/60 hover:bg-white/10"
              }`}
            >
              <UserPlus className="w-5 h-5" /> Sign Up
            </button>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-5">
            {!isLogin && (
              <div>
                <label className="block text-sm font-medium text-white/70 mb-2">Full Name</label>
                <div className="relative">
                  <User className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/40" />
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="John Doe"
                    required={!isLogin}
                    className="w-full bg-white/10 border border-white/20 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
                  />
                </div>
              </div>
            )}

            <div>
              <label className="block text-sm font-medium text-white/70 mb-2">Email Address</label>
              <div className="relative">
                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/40" />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="you@school.edu"
                  required
                  className="w-full bg-white/10 border border-white/20 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-white/70 mb-2">Password</label>
              <div className="relative">
                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/40" />
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="••••••••"
                  required
                  className="w-full bg-white/10 border border-white/20 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all"
                />
              </div>
            </div>

            {!isLogin && (
              <>
                <div className="flex items-center gap-3">
                  <input
                    type="checkbox"
                    id="hasCode"
                    checked={hasAccessCode}
                    onChange={(e) => setHasAccessCode(e.target.checked)}
                    className="w-5 h-5 rounded bg-white/10 border-white/30 text-purple-500"
                  />
                  <label htmlFor="hasCode" className="text-sm text-white/70 flex items-center gap-2">
                    <GraduationCap className="w-4 h-4" /> I have a school access code
                  </label>
                </div>

                {hasAccessCode && (
                  <div>
                    <label className="block text-sm font-medium text-white/70 mb-2">Access Code</label>
                    <div className="relative">
                      <Key className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/40" />
                      <input
                        type="text"
                        name="accessCode"
                        value={formData.accessCode}
                        onChange={handleChange}
                        placeholder="ABC123DEF"
                        className="w-full bg-white/10 border border-white/20 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/40 focus:outline-none focus:ring-2 focus:ring-purple-500 font-mono"
                      />
                    </div>
                  </div>
                )}
              </>
            )}

            {error && (
              <div className="p-4 bg-red-500/10 border border-red-500/30 rounded-xl text-red-300 text-sm">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 disabled:opacity-50 py-4 rounded-xl font-bold text-white shadow-lg hover:shadow-xl transition-all flex items-center justify-center gap-3"
            >
              {loading ? (
                <>
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                  {isLogin ? "Logging in…" : "Creating account…"}
                </>
              ) : (
                <>
                  {isLogin ? <LogIn className="w-5 h-5" /> : <UserPlus className="w-5 h-5" />}
                  {isLogin ? "Login" : "Create Account"}
                </>
              )}
            </button>
          </form>

          {!isLogin && !hasAccessCode && (
            <div className="mt-6 p-4 bg-blue-500/10 border border-blue-400/20 rounded-xl text-sm text-blue-200">
              <p className="font-semibold mb-1">Without an access code:</p>
              <p className="text-blue-200/70">You'll get 3 free uses. Upgrade for unlimited access!</p>
            </div>
          )}
        </div>

        <p className="text-center text-white/40 text-sm mt-6">
          By continuing, you agree to our Terms of Service
        </p>
      </div>
    </div>
  );
}
