import React, { useState } from "react";
import {
  LogIn,
  UserPlus,
  Mail,
  Lock,
  User,
  Key,
  GraduationCap,
  Shield,
  CheckCircle,
  ArrowLeft
} from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function AuthPage({ initialMode = "login" }) {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(initialMode === "login");
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    accessCode: ""
  });
  const [hasAccessCode, setHasAccessCode] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      // Your actual login/register logic here
    }, 1500);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Simple Header */}
      <header className="bg-white border-b border-gray-200">
        <div className="container mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <button 
              onClick={() => navigate('/')}
              className="flex items-center gap-2 text-gray-600 hover:text-gray-900 transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
              <span className="text-sm">Back to Home</span>
            </button>
          </div>
          <div className="flex items-center gap-2">
            <img src="/assets/logo.png" alt="NextLogic AI" className="h-10 w-10" />
            <span className="text-xl font-black text-gray-900">NextLogic AI</span>
          </div>
          <div className="w-24"></div> {/* Spacer for centering */}
        </div>
      </header>

      {/* Main Content */}
      <div className="flex-1 flex items-center justify-center p-4 py-12">
        <div className="w-full max-w-md">
          {/* Card */}
          <div className="bg-white rounded-2xl shadow-xl border border-gray-200 overflow-hidden">
            {/* Top Accent */}
            <div className="h-2 bg-gradient-to-r from-purple-600 to-pink-600"></div>
            
            <div className="p-8">
              {/* Header */}
              <div className="text-center mb-8">
                <h1 className="text-3xl font-black text-gray-900 mb-2">
                  {isLogin ? "Welcome Back" : "Get Started"}
                </h1>
                <p className="text-gray-600">
                  {isLogin 
                    ? "Log in to your teacher dashboard" 
                    : "Join thousands of teachers maintaining academic integrity"}
                </p>
              </div>

              {/* Toggle Tabs */}
              <div className="flex gap-2 bg-gray-100 p-1 rounded-xl mb-6">
                <button
                  onClick={() => setIsLogin(true)}
                  className={`flex-1 py-2.5 rounded-lg font-semibold text-sm transition-all ${
                    isLogin
                      ? "bg-white text-gray-900 shadow-sm"
                      : "text-gray-600 hover:text-gray-900"
                  }`}
                >
                  <LogIn className="w-4 h-4 inline mr-2" />
                  Login
                </button>
                <button
                  onClick={() => setIsLogin(false)}
                  className={`flex-1 py-2.5 rounded-lg font-semibold text-sm transition-all ${
                    !isLogin
                      ? "bg-white text-gray-900 shadow-sm"
                      : "text-gray-600 hover:text-gray-900"
                  }`}
                >
                  <UserPlus className="w-4 h-4 inline mr-2" />
                  Sign Up
                </button>
              </div>

              {/* Form */}
              <div className="space-y-4">
                {!isLogin && (
                  <div>
                    <label className="block text-sm font-semibold text-gray-700 mb-2">
                      Full Name
                    </label>
                    <div className="relative">
                      <User className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                      <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        placeholder="Jane Smith"
                        className="w-full bg-white border border-gray-300 rounded-lg pl-11 pr-4 py-3 text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                      />
                    </div>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Email Address
                  </label>
                  <div className="relative">
                    <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="teacher@school.edu"
                      className="w-full bg-white border border-gray-300 rounded-lg pl-11 pr-4 py-3 text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-700 mb-2">
                    Password
                  </label>
                  <div className="relative">
                    <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="••••••••"
                      className="w-full bg-white border border-gray-300 rounded-lg pl-11 pr-4 py-3 text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                    />
                  </div>
                </div>

                {!isLogin && (
                  <>
                    <div className="flex items-center gap-3 bg-blue-50 border border-blue-200 rounded-lg p-3">
                      <input
                        type="checkbox"
                        id="hasCode"
                        checked={hasAccessCode}
                        onChange={(e) => setHasAccessCode(e.target.checked)}
                        className="w-4 h-4 rounded text-purple-600 focus:ring-purple-500"
                      />
                      <label htmlFor="hasCode" className="text-sm text-gray-700 flex items-center gap-2 cursor-pointer">
                        <GraduationCap className="w-4 h-4" />
                        I have a school access code
                      </label>
                    </div>

                    {hasAccessCode && (
                      <div>
                        <label className="block text-sm font-semibold text-gray-700 mb-2">
                          School Access Code
                        </label>
                        <div className="relative">
                          <Key className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                          <input
                            type="text"
                            name="accessCode"
                            value={formData.accessCode}
                            onChange={handleChange}
                            placeholder="SCHOOL-2024-ABC"
                            className="w-full bg-white border border-gray-300 rounded-lg pl-11 pr-4 py-3 text-gray-900 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent font-mono uppercase"
                          />
                        </div>
                      </div>
                    )}
                  </>
                )}

                {error && (
                  <div className="p-3 bg-red-50 border border-red-200 rounded-lg text-red-700 text-sm">
                    {error}
                  </div>
                )}

                <button
                  onClick={handleSubmit}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 disabled:opacity-50 disabled:cursor-not-allowed py-3.5 rounded-lg font-bold text-white shadow-lg hover:shadow-xl transition-all flex items-center justify-center gap-2"
                >
                  {loading ? (
                    <>
                      <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                      {isLogin ? "Logging in..." : "Creating account..."}
                    </>
                  ) : (
                    <>
                      {isLogin ? <LogIn className="w-5 h-5" /> : <UserPlus className="w-5 h-5" />}
                      {isLogin ? "Login to Dashboard" : "Create Free Account"}
                    </>
                  )}
                </button>
              </div>

              {/* Benefits/Trust Signals */}
              {!isLogin && (
                <div className="mt-6 p-4 bg-gradient-to-r from-green-50 to-blue-50 border border-green-200 rounded-lg">
                  <p className="font-semibold text-gray-900 mb-2 flex items-center gap-2 text-sm">
                    <CheckCircle className="w-4 h-4 text-green-600" />
                    {hasAccessCode ? "School Plan Benefits:" : "Free Trial Includes:"}
                  </p>
                  <ul className="text-gray-700 text-sm space-y-1 ml-6 list-disc">
                    {hasAccessCode ? (
                      <>
                        <li>Unlimited AI monitoring</li>
                        <li>Full feature access</li>
                        <li>Priority support</li>
                      </>
                    ) : (
                      <>
                        <li>3 free monitored sessions</li>
                        <li>All core features</li>
                        <li>No credit card required</li>
                      </>
                    )}
                  </ul>
                </div>
              )}

              {/* Footer Links */}
              <div className="mt-6 text-center">
                <p className="text-gray-500 text-xs">
                  By continuing, you agree to our{" "}
                  <a href="/terms" className="text-purple-600 hover:text-purple-700 underline">
                    Terms
                  </a>{" "}
                  and{" "}
                  <a href="/privacy" className="text-purple-600 hover:text-purple-700 underline">
                    Privacy Policy
                  </a>
                </p>
              </div>
            </div>
          </div>

          {/* Security Badge */}
          <div className="mt-6 text-center">
            <p className="text-gray-500 text-sm flex items-center justify-center gap-2">
              <Shield className="w-4 h-4 text-green-600" />
              Your data is encrypted and FERPA compliant
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
