// src/components/AuthPage.js
import React, { useState, useEffect } from "react";
import {
  LogIn,
  UserPlus,
  Sparkles,
  Mail,
  Lock,
  User,
  Key,
  GraduationCap,
} from "lucide-react";
import { useAuth } from "../AuthContext";   // ← your context file
import { useNavigate } from "react-router-dom"; // or your router

export default function AuthPage() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    accessCode: "",
  });
  const [hasAccessCode, setHasAccessCode] = useState(false);

  const { login, register, loading, error, user } = useAuth();
  const navigate = useNavigate();

  // -----------------------------------------------------------------
  // Redirect when logged in
  // -----------------------------------------------------------------
  useEffect(() => {
    if (user) {
      navigate("/dashboard", { replace: true });
    }
  }, [user, navigate]);

  // -----------------------------------------------------------------
  // Form helpers
  // -----------------------------------------------------------------
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isLogin) {
      await login(formData.email, formData.password);
    } else {
      await register(
        formData.name,
        formData.email,
        formData.password,
        hasAccessCode ? formData.accessCode : null
      );
      // Auto‑switch to login after successful registration
      setIsLogin(true);
    }
  };

  // -----------------------------------------------------------------
  // UI
  // -----------------------------------------------------------------
  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-6">
      {/* Subtle animated blobs */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div
          className="absolute top-1/4 left-1/4 w-96 h-96 bg-purple-500/20 rounded-full blur-3xl"
          style={{ animation: "pulse 4s ease-in-out infinite" }}
        />
        <div
          className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-cyan-500/20 rounded-full blur-3xl"
          style={{ animation: "pulse 4s ease-in-out infinite", animationDelay: "2s" }}
        />
      </div>

      {/* Card */}
      <div className="w-full max-w-md relative z-10">
        {/* Logo */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-purple-500 to-pink-500 rounded-3xl mb-4 shadow-2xl shadow-purple-500/50">
            <Sparkles className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
            NextLogicAI
          </h1>
          <p className="text-white/60">AI‑Powered Educational Platform</p>
        </div>

        {/* Glass container */}
        <div className="bg-white/5 backdrop-blur-xl rounded-3xl p-8 border border-white/10 shadow-2xl">
          {/* Toggle */}
          <div className="flex gap-2 bg-white/5 p-2 rounded-2xl mb-8">
            <button
              onClick={() => setIsLogin(true)}
              className={`flex-1 py-3 rounded-xl font-semibold flex items-center justify-center gap-2 transition-all ${
                isLogin
                  ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg"
                  : "text-white/60 hover:bg-white/10"
              }`}
            >
              <LogIn className="w-4 h-4" /> Login
            </button>
            <button
              onClick={() => setIsLogin(false)}
              className={`flex-1 py-3 rounded-xl font-semibold flex items-center justify-center gap-2 transition-all ${
                !isLogin
                  ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow-lg"
                  : "text-white/60 hover:bg-white/10"
              }`}
            >
              <UserPlus className="w-4 h-4" /> Sign Up
            </button>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-5">
            {/* Name – only on Sign Up */}
            {!isLogin && (
              <div>
                <label className="block text-sm font-semibold mb-2 text-white/60">
                  Full Name
                </label>
                <div className="relative">
                  <User className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="John Doe"
                    required={!isLogin}
                    className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                  />
                </div>
              </div>
            )}

            {/* Email */}
            <div>
              <label className="block text-sm font-semibold mb-2 text-white/60">
                Email Address
              </label>
              <div className="relative">
                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="you@school.edu"
                  required
                  className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                />
              </div>
            </div>

            {/* Password */}
            <div>
              <label className="block text-sm font-semibold mb-2 text-white/60">
                Password
              </label>
              <div className="relative">
                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="••••••••"
                  required
                  className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all"
                />
              </div>
            </div>

            {/* Access Code – Sign Up only */}
            {!isLogin && (
              <>
                <div className="flex items-center gap-3">
                  <input
                    type="checkbox"
                    id="hasCode"
                    checked={hasAccessCode}
                    onChange={(e) => setHasAccessCode(e.target.checked)}
                    className="w-5 h-5 rounded bg-white/5 border-white/20 text-indigo-500 focus:ring-indigo-500"
                  />
                  <label htmlFor="hasCode" className="text-sm text-white/60 flex items-center gap-2">
                    <GraduationCap className="w-4 h-4" /> I have a school access code
                  </label>
                </div>

                {hasAccessCode && (
                  <div>
                    <label className="block text-sm font-semibold mb-2 text-white/60">
                      Access Code
                    </label>
                    <div className="relative">
                      <Key className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-white/30" />
                      <input
                        type="text"
                        name="accessCode"
                        value={formData.accessCode}
                        onChange={handleChange}
                        placeholder="ABC123DEF"
                        className="w-full bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 py-3 text-white placeholder-white/30 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent transition-all font-mono"
                      />
                    </div>
                  </div>
                )}
              </>
            )}

            {/* Error / Success */}
            {error && (
              <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/30 text-red-300 text-sm">
                {error}
              </div>
            )}

            {/* Submit */}
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-gradient-to-r from-indigo-600 to-purple-600 hover:from-indigo-700 hover:to-purple-700 disabled:from-gray-600 disabled:to-gray-700 disabled:cursor-not-allowed py-4 rounded-xl font-bold text-lg text-white shadow-lg hover:shadow-xl hover:scale-105 transition-all duration-300 flex items-center justify-center gap-3"
            >
              {loading ? (
                <>
                  <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                  {isLogin ? "Logging in…" : "Creating account…"}
                </>
              ) : (
                <>
                  {isLogin ? <LogIn className="w-5 h-5" /> : <UserPlus className="w-5 h-5" />}
                  {isLogin ? "Login" : "Create Account"}
                </>
              )}
            </button>
          </form>

          {/* Free‑use hint */}
          {!isLogin && !hasAccessCode && (
            <div className="mt-6 p-4 bg-blue-500/10 border border-blue-400/20 rounded-xl text-sm text-blue-200">
              <p className="font-semibold mb-1">Without an access code:</p>
              <p className="text-blue-200/70">You'll get 3 free uses. Upgrade for unlimited access!</p>
            </div>
          )}
        </div>

        <p className="text-center text-white/30 text-sm mt-6">
          By continuing, you agree to our Terms of Service
        </p>
      </div>
    </div>
  );
}

